<script>
$(".reg-btn").click(function()
{
    window.location.href="?r=register/register_info";
})
$("#weixin").click(function()
{
    $.ajax({
        url:'?r=site/erweima1&c='+1,
        Type:"get",
        success:function(obj)
        {
            var nNode = document.getElementById('ccc');
            nNode.style.display = "none";
            var nNode = document.getElementById('ccc1');
            nNode.style.display = "";
            $("#ccc1").html(obj)
        }
    });
})
$(".btnP2").click(function()
{
    //获取文本框的值
    var user = $("#jcLoginName").val();
    var pwd = $("#jcLoginPass").val();
    var check = $("#nextLogin").prop("checked");
    // var $errorTips = $loginForm.find('.error-tips');
    if(user == '' || user == null)
    {
        $(".error-tips").css({
            left: '0px'
        }).html('用户名不能为空').show();
        return false;  
    }
    if(pwd == '' || pwd == null)
    {
         $(".error-tips").css({
                left: '205px'
            }).html('请输入密码').show();
         return false;
    }
    $.getJSON('?r=login/login',{'user':user,'pwd':pwd,'check':check},function(obj){
        if(obj.code == 1)
        {
            $(".error-tips").css({
                left: '205px'
            }).html('密码错误').show();
        }
        else if(obj.code == 0)
        {
            $(".error-tips").css({
                left: '0px'
            }).html('用户名错误').show();
        }
        else
        {
            window.location.href="?r=index/index";
        }
    })
})
</script>
